#_SALTERID=65
_CALTERID=0
_PATH_LENG=65

_UUID="$(uuidgen)"
_S_LISTENPORT="$(shuf -i 32000-62999 -n 1)"
_LOGLEVEL='none'

_MUX_ENABLED='false'
_MUX_CONCURRENCY='16'

###############################################################################

_NETWORK_PATH="$(cat /dev/urandom | tr -dc '0-9a-zA-Z' | fold --width="${_PATH_LENG}" | head -100 | sort --random-sort | head -1)"

# "id": "00000000-0000-0000-0000-000000000000",
# "alterId": 0
sed "s/\"id\": \".*/\"id\": \"${_UUID}\",/g" -i *config.json

#sed "s/\"alterId\": .*/\"alterId\": ${_SALTERID},/g" -i server-config.json
sed -e '/"alterId":/d' -i server-config.json
sed "s/\"alterId\": .*/\"alterId\": ${_CALTERID},/g" -i client-config.json

sed "s/\"loglevel\": \".*/\"loglevel\": \"${_LOGLEVEL}\",/g" -i *config.json

# "path": "/00000000-0000-0000-0000-000000000000"
sed "s/\"path\": \"\/.*/\"path\": \"\/${_NETWORK_PATH}\",/g" -i *config.json

# "listen": "127.0.0.1", 只监听 127.0.0.1，避免除本机外的机器探测到开放了 ${_S_LISTENPORT} 端口
# server listening port
sed 's/"listen":.*/"listen": "127.0.0.1",/g' -i *config.json
sed "s/\"port\": .*/\"port\": ${_S_LISTENPORT},/g" -i server-config.json

# mux
sed "/\"mux\": {/{n;s/\"enabled\": .*/\"enabled\": ${_MUX_ENABLED},/g}" -i client-config.json
sed "s#\"concurrency\": .*#\"concurrency\": ${_MUX_CONCURRENCY}#g" -i client-config.json

# delete , in the line before }
for i in $(grep -n -B 1 ' }' *.json | awk '{print $1}' | grep ':' | cut -d: -f1 | sort -V | uniq | awk 'BEGIN{RS="#";OFS=" "}NF=NF'); do
    for m in $(grep -n -B 1 ' }' ${i} | sed -e '/--/d' | grep '\-' | awk '{print $1}' | sed 's/-//g' | awk 'BEGIN{RS="#";OFS=" "}NF=NF'); do
        sed "${m}s/,$//g" -i ${i}
    done
done

grep --color=auto '"id":' *config.json
grep --color=auto '"path":' *config.json
grep --color=auto '"loglevel":' *config.json
